import React,{useEffect, useState}from 'react';
import './nav.css'
import logo from './logo.png'
import {Link,animateScroll as scroll} from "react-scroll" 


function Nav() {
  const[showLinks,setShowLinks]= useState(false);
  const[navbar,setNavbar]=useState(false);

  const changeBackground=()=>{
 if(window.scrollY>=30){
   setNavbar(true)
 }else{
   setNavbar(false)
 }
  };
  window.addEventListener('scroll',changeBackground)
    return (
      
    
        
        <div className={navbar ? 'navContaine active':'navContainer  active'}>
            <a href='/home'>
              <img src={logo} className='navLogo'alt='logo'></img></a> 
             <div className="links" id={showLinks ? "hidden" : ""}>
          <Link   
          activeClass="active"
          to="service"
          spy={true}
          smooth={true}
           duration={200}
           className="underline">
          Service
          </Link>
          <Link   
          activeClass="active"
          to="aboutus"
          spy={true}
          smooth={true}
          duration={200}
          className="underline">
          About Us
          </Link>
          <Link   
          activeClass="active"
          to="ourWork"
          spy={true}
          smooth={true}
           duration={200}
           className="underline">
         OurWork
          </Link>
          <Link   
          activeClass="active"
          to="contact"
          spy={true}
          smooth={true}
           duration={200}
           className="underline">
       contact
          </Link>
    
     
          
        
         
           
             </div>
          <button onClick={()=>setShowLinks(!showLinks)} className='navMenu'>menu</button>
            </div>
           
            );
}

export default Nav;
