import React, { useState } from 'react';

import {FaUserAlt,FaPhoneAlt} from "react-icons/fa";
import { FiMail } from "react-icons/fi";
import '../src/css/contact.css';
import Axios from 'axios';
import ContactLogo from './contactlogo.png'



function Contact() {


   
    const[data,setData]=useState({
        name:"",
        email:"",
        phone:"",
        message:"",
       
    })
    function handle(e){
        const newData={...data}
        newData[e.target.id]=e.target.value
        setData(newData)
       

    }
    function submit(e){
       
        Axios.post("/api/contact/",{
                name:data.fname,
                email:data.email,
                phone:data.number,
                message:data.message,
                alert_message:data.sucsess

            }).then(res=>{
                console.log(res.data)
            })
        

    }
  
 
    return (
        <div className="Contactcontainer" id="contact">
        
           
            <div className='contactContent'>
            <h4 className="contactTitle">Want to Work with Us?</h4>
            
            <img  src={ContactLogo} className="contactLogo" alt="contactLogo"/>
           
             <form  className="contactForm">
             
                <FaUserAlt style={{color:"white",textAlign:"center",minWidth:"50px",minHeight:"15px",padding:"1px",marginLeft:"1rem"}}/>
                <input onChange={(e)=>handle(e)} value={data.fname} type="text" id="fname"  placeholder="Enter your Name.." className="inputField" />

               <FiMail style={{color:"white",textAlign:"center",minWidth:"50px",minHeight:"25px",padding:"2px",marginLeft:"1rem"}}/>
                <input onChange={(e)=>handle(e)} value={data.email} type="text" id="email"  placeholder="Enter your Email.." className="inputField" />

               <FaPhoneAlt style={{ color:"white",textAlign:"center",minWidth:"50px",minHeight:"15px",padding:"5px",marginLeft:"1rem"}}/>
                <input onChange={(e)=>handle(e)} value={data.number} type="number" id="number"  placeholder="Enter your Phone Number.." className="inputField" />

               <textarea onChange={(e)=>handle(e)} value={data.message} id="message"  placeholder="Write your Queries.." type="text"></textarea>
              
               <button type="submit" className='submit'>Submit</button>

            </form>
            

            </div>
        
       

   </div>
 
      
  
    );
}

export default Contact;
