import React, { useState } from 'react';

import {FaUserAlt,FaPhoneAlt} from "react-icons/fa";
import { FiMail } from "react-icons/fi";
import '../src/css/contact.css';
import axios from 'axios';
import ContactLogo from './contactlogo.png'



function Contact() {


   
    const[data,setData]=useState({
        name:"",
        email:"",
        phone:"",
        message:"",
       
    })
    function handelChange(event) {
        const{datas,value}=event.target;
    
        setData(prevInput=>{
          return{
            ...prevInput,
            [datas]:value
          }
        })
        
      }
    function handleClick() {
        const newNote={
          email: data.email,
          phone:data.phone,
          name:data.name,
          message:data.message
        }
        axios.post("http://localhost:8000/api/contact/")
        .then(res=>{
          console.log(res)
          newNote(res.data)
        })
        .catch(err=>{
          console.log(err)
        })
       
        
      }
  
 
    return (
        <div className="Contactcontainer" id="contact">
        
           
            <div className='contactContent'>
            <h4 className="contactTitle">Want to Work with Us?</h4>
            
            <img  src={ContactLogo} className="contactLogo" alt="contactLogo"/>
           
             <form onSubmit={handleClick} className="contactForm">
             
                <FaUserAlt style={{color:"white",textAlign:"center",minWidth:"50px",minHeight:"15px",padding:"1px",marginLeft:"1rem"}}/>
                <input onChange={handelChange} value={data.fname} type="text" id="fname"  placeholder="Enter your Name.." className="inputField" />
                
               <FiMail style={{color:"white",textAlign:"center",minWidth:"50px",minHeight:"25px",padding:"2px",marginLeft:"1rem"}}/>
                <input onChange={handelChange} value={data.emails} type="text" id="emails"  placeholder="Enter your Email.." className="inputField" />

               <FaPhoneAlt style={{ color:"white",textAlign:"center",minWidth:"50px",minHeight:"15px",padding:"5px",marginLeft:"1rem"}}/>
                <input onChange={handelChange}  value={data.number} type="number" id="number"  placeholder="Enter your Phone Number.." className="inputField" />

               <textarea onChange={handelChange}  value={data.messages} id="messages"  placeholder="Write your Queries.." type="text"></textarea>
              
               <button type="submit" className='submit'>Submit</button>

            </form>
            

            </div>
        
       

   </div>
 
      
  
    );
}

export default Contact;
