import React, { useState } from 'react';

import {FaUserAlt,FaPhoneAlt} from "react-icons/fa";
import { FiMail } from "react-icons/fi";
import '../src/css/contact.css';
import axios from 'axios';
import ContactLogo from './contactlogo.png'



function Contact() {
  const[data,setData]=useState({
    email:'',
    name:'',
    phone:'',
    message:''
  })
function handelChange(e){
  const{name,value}=e.target;

  setData(preInput=>{
    return{
      ...preInput,
      [name]:value
    }
  })
}
function handleSubmit(){
  const newNote={
    email:data.email,
    phone:data.phone,
    message:data.message,
    name:data.name

  }
  axios.post('"http://localhost:8000/api/contact/"',newNote)
}

 
 
    return (
        <div className="Contactcontainer" id="contact">
        
           
            <div className='contactContent'>
            <h4 className="contactTitle">Want to Work with Us?</h4>
            
            <img  src={ContactLogo} className="contactLogo" alt="contactLogo"/>
           
             <form onSubmit={handleSubmit}  className="contactForm">
             
                <FaUserAlt style={{color:"white",textAlign:"center",minWidth:"50px",minHeight:"15px",padding:"1px",marginLeft:"1rem"}}/>
                <input onChange={handelChange(e)}   type="text" value={data.fname} name="fname"  placeholder="Enter your Name.." className="inputField" />
                
               <FiMail style={{color:"white",textAlign:"center",minWidth:"50px",minHeight:"25px",padding:"2px",marginLeft:"1rem"}}/>
                <input onChange={handelChange}   type="text" value={data.emails} name="emails"  placeholder="Enter your Email.." className="inputField" />

               <FaPhoneAlt style={{ color:"white",textAlign:"center",minWidth:"50px",minHeight:"15px",padding:"5px",marginLeft:"1rem"}}/>
                <input onChange={handelChange}    type="number" value={data.number} name="number"  placeholder="Enter your Phone Number.." className="inputField" />

               <textarea onChange={handelChange}  value={data.message} name="message"  placeholder="Write your Queries.." type="text"></textarea>
              
               <button type="submit"  className='submit'>Submit</button>

            </form>
            

            </div>
        
       

   </div>
 
      
  
    );
}

export default Contact;
