import React, { useState } from 'react';
import contact from './contact.png'
import {FaUserAlt,FaPhoneAlt} from "react-icons/fa";
import { FiMail } from "react-icons/fi";
import '../src/css/contact.css';
import Axios from 'axios';



function Contact() {
    const url="http://127.0.0.1:8000/contact/";
    const[data,setData]=useState({
        name:"",
        email:"",
        phone:"",
        message:""
    })
    function handle(e){
        e.prventDefault();
        const newData={...data}
        newData[e.target.id]=e.target.value
        setData(newData)
       

    }
    function submit(e){
       
        Axios.post(
            url,{
                name:data.fname,
                email:data.email,
                phone:data.number,
                message:data.message

            }).then(res=>{
                this.setData({alert_message:"Success"})
            }).catch(error=>{
                this.setData({alert_message:"error occured"})
            })
        

    }
  
 
    return (
        <div className="Contactcontainer" id="contact">
        <div className="ContactTitle">
            <img src={contact}/>
            </div>
           
            <div className='contactContent'>
           
             <form onSubmit={(e)=>submit(e)}>
             <h4>Contact Us</h4>
           
               <FaUserAlt style={{textAlign:"center",minWidth:"50px",minHeight:"15px",padding:"5px",marginLeft:"1rem"}}/>
                <input onChange={(e)=>handle(e)} value={data.fname} type="text" id="fname"  placeholder="Your name.." className="inputField" />

               <FiMail style={{textAlign:"center",minWidth:"50px",minHeight:"25px",padding:"2px",marginLeft:"1rem"}}/>
                <input onChange={(e)=>handle(e)} value={data.email} type="text" id="email"  placeholder="Email.." className="inputField" />

               <FaPhoneAlt style={{textAlign:"center",minWidth:"50px",minHeight:"15px",padding:"5px",marginLeft:"1rem"}}/>
                <input onChange={(e)=>handle(e)} value={data.number} type="number" id="number"  placeholder="Phone Number.." className="inputField" />

               <textarea onChange={(e)=>handle(e)} value={data.message} id="message"  placeholder="Send Message.." type="text"></textarea>
               <button type="submit" className='submit'>Submit</button>

            </form>
            

            </div>
        
       

   </div>
 
      
  
    );
}

export default Contact;
